create table Users(UserId SERIAL Primary Key,FirstName text,LastName text,PhoneNumber text,Email text,DOB DATE NOT NULL,Logged text,LogTime DATE NOT NULL,Online boolean,CreationTime DATE NOT NULL DEFAULT CURRENT_DATE,ModifiedTime DATE NOT NULL,Status boolean);
Create table Roles(RoleId SERIAL Primary Key,RoleName text,UserId integer REFERENCES Users(UserId) ,CreationTime DATE NOT NULL DEFAULT CURRENT_DATE,ModifiedTime DATE NOT NULL,Status boolean);
Create table DomainLogs(LogId SERIAL Primary Key,LogHeader text,LogCode text,LogType text,Message text,ReferenceId integer,LoggedFor text,CreationTime DATE NOT NULL DEFAULT CURRENT_DATE,ModifiedTime DATE NOT NULL,Status boolean);
Create table Courses(CourseId SERIAL Primary Key,Name text,Description text,CreationTime DATE NOT NULL DEFAULT CURRENT_DATE,ModifiedTime DATE NOT NULL,Status boolean);
Create table Questions(QuestionId SERIAL Primary Key,CourseId integer REFERENCES Courses(CourseId),Title text,Question text,Option1 text,Option2 text,Option3 text,Option4 text,Answer text,CreatedBy integer REFERENCES Users(UserId),Modified integer REFERENCES Users(UserId),CreationTime DATE NOT NULL DEFAULT CURRENT_DATE,ModifiedTime DATE NOT NULL,Status boolean);
Create table Tests(TestId SERIAL Primary Key,UserId integer REFERENCES Users(UserId),Duration integer,Quantity integer,PME integer,NME integer,TotalMarks integer,MarksObtained integer,FinalizedTime timestamp,CreationTime DATE NOT NULL DEFAULT CURRENT_DATE,ModifiedTime DATE NOT NULL,Status boolean);
Create table TestDetails(TesterId SERIAL Primary Key,TestId integer REFERENCES Tests(TestId),QuestionId integer REFERENCES Questions(QuestionId),ChoosedOption text,BookMarked boolean,CreationTime DATE NOT NULL DEFAULT CURRENT_DATE,ModifiedTime DATE NOT NULL,Status boolean);